			cache[unit] = [];
		}
		const unitCache = cache[unit];

		const year = rawValue.getFullYear();
		let yearCache = unitCache[year];
		if(!yearCache){
			yearCache = unitCache[year] = {};
		}
		yearCache[timestamp] = value;
	}
