	};

	gantt.attachEvent("onDataRender", function() {
		const emptyStateElement = gantt.ext.emptyStateElement;
		if (emptyStateElement.isEnabled() && emptyStateElement.isGanttEmpty()) {
			emptyStateElement.show();
		} else {
			emptyStateElement.hide();
		}
	});




